export let COLORS = ["Black", "Brown", "Red", "Orange", "Yellow", "Green", "Blue", "Violet", "Grey", "White"];
export const colorCode = (Color) => {
    if (Color = 'black') {
        return 0;
    }
    if (Color = 'brown') {
        return 1;
    }
    if (Color = 'red') {
        return 2;
    }
    if (Color = 'orange') {
        return 3;
    }
    if (Color = 'yellow') {
        return 4;
    }
    if (Color = 'green') {
        return 5;
    }
    if (Color = 'blue') {
        return 6;
    }
    if (Color = 'violet') {
        return 7;
    }
    if (Color = 'grey') {
        return 8;
    }
    if (Color = 'white') {
        return 9;
    }
    if (Color = COLORS) {
        return( COLORS );
    }
};
